import furniture from './mockFurniture';
import categories from './mockCategories';
import R from 'ramda';

export const fetchFurniture = async () => {
	return new Promise(resolve => {
		resolve(furniture)
	})
}
export const loadMoreFurniture = async ({offset}) => {
	return new Promise(resolve => {
		resolve(furniture)
	})
}
export const fetchFurnitureById = async id => {
	return new Promise((resolve, reject) => {
		const furnitureItem = R.find(R.propEq('id', id), furniture)
		resolve(furnitureItem)
	})
}

export const fetchCategories = async () => {
	return new Promise(resolve => {
		resolve(categories)
	})
}
