import React from 'react';
import {Link, withRouter} from 'react-router';
import { connect } from 'react-redux';
import { getCategories, getActiveCategoryId } from 'selectors';
import {compose} from 'redux';


const Categories = ({categories, activeCategoryId})=> {
	console.log(activeCategoryId)
	return (
		<ul id="menu">
			{categories.map((category, index) => 
				<Link key={index} onClick={() => console.log(category.id)} to={`/furniture/categories/${category.id}`}><li>{category.name}</li></Link>
			)}
		</ul>
	)
}

const mapStateToProps = (state, ownProps) => ({
	categories: getCategories(state), 
	activeCategoryId: getActiveCategoryId(ownProps)
})

export default compose(
	withRouter,
	connect(mapStateToProps, null))	(Categories);
// export default connect(mapStateToProps)(Categories);