import R from 'ramda';

export const getFurnitureById = (state, id) => R.prop(id, state.furniture)
export const getActiveCategoryId = ownProps => R.path(['params', 'id'], ownProps);
export const getFurniture = (state, ownProps) => {
	const activeCategoryId = getActiveCategoryId(ownProps)
	const applySearch = item => R.contains(
		state.furniturePage.search,
		R.prop('name', item)
	)
	const applyCategory = item => R.equals(
		activeCategoryId,
		R.prop('categoryId', item)
	)

	const furniture = R.compose(
		R.filter(applySearch),
		R.when(R.always(activeCategoryId), R.filter(applyCategory)),
		R.map(id => getFurnitureById(state, id))
	)(state.furniturePage.ids)
	console.log(furniture)
	return (activeCategoryId === '0') ? R.values(state.furniture) : furniture;
}

export const getRenderedFurnitureLength = state => R.length(state.furniturePage.ids);

export const getTotalBasketCount = state => R.length(state.basket);

export const getTotalBasketPrice = state => {
	const totalPrice = R.compose(
		R.sum,
		R.pluck('price'),
		R.map(id => getFurnitureById(state,id))
	)(state.basket)
	return totalPrice;
}
export const getCategories = state => R.values(state.categories);

export const getBasketFurnitureWithCount = state => {
	const uniqueIds = R.uniq(state.basket)
	const furnitureCount = id => R.compose(
		R.length,
		R.filter(basketId => R.equals(id, basketId))
	)(state.basket)
	const furnitureWithCount = furniture => R.assoc('count', furnitureCount(furniture.id), furniture)
	const furniture = R.compose(
		R.map(furnitureWithCount),
		R.map(id => getFurnitureById(state, id))
	)(uniqueIds)
	return furniture;
}
